+@import 'components/focusable/style';
+## Focusable
+This component lets you wrap complex content in an accessible, clickable wrapper. `Focusable` should be used in place of putting an `onClick` event on a div. It adds [the "button" ARIA role](https://developer.mozilla.org/en-US/docs/Web/Accessibility/ARIA/ARIA_Techniques/Using_the_button_role), for screen reader support, and enables keyboard support for keyboard-only accessibility.
+**Think about your markup before using this component!** You probably don't need it, for example:
+If you want to add an action to a larger element, for example the Language Picker, you can use this component. Be aware that a screen reader will read the entire contents of `Focusable` to the user, so _try_ to keep the contents short and actionable.
+#### How to use:
+```jsx
+import Focusable from 'components/focusable';
+export default function InteractiveItem() {
+	return (
+		<Focusable onClick={ func }>
+			// Some complex display, may contain divs, images, other components, etc.
+		</Focusable>
+	);
+}
+```
+#### Props
+* `onClick`: (func) Function called when the button is clicked, or when focused and enter or space are pressed. Required.
+* `onKeyDown`: (func) Function called when focused and any other keyboard event happens (useful for arrow navigation)
+/** @format */
+/**
+ * External dependencies
+ */
+import React from 'react';
+import Gridicon from 'gridicons';
+import { noop } from 'lodash';
+/**
+ * Internal dependencies
+ */
+import Card from 'components/card';
+import config from 'config';
+import DocsExample from 'devdocs/docs-example';
+import Focusable from 'components/focusable';
+import ScreenReaderText from 'components/screen-reader-text';
+class FocusableExample extends React.PureComponent {
+	static displayName = 'Focusable';
+	render() {
+		return config.isEnabled( 'devdocs/components-usage-stats' )
+			? this.renderDocsExampleWithUsageStats()
+			: this.renderFocusable();
+	}
+	renderDocsExampleWithUsageStats = () => {
+		return (
+			<DocsExample componentUsageStats={ this.props.componentUsageStats }>
+				{ this.renderFocusable() }
+			</DocsExample>
+		);
+	};
+	renderFocusable = () => {
+		return (
+			<Card>
+				<Focusable onClick={ noop }>
+					<p>
+						This keyboard-accessible component can contain other elements as children, including{' '}
+						<code>p</code>s, <code>div</code>s, or other components.
+					</p>
+				</Focusable>
+				<p>
+					An example of real use is in the Language Picker, which can be considered a "complex
+					button."
+				</p>
+				<Focusable onClick={ noop } className="language-picker">
+					<div className="language-picker__icon" aria-hidden>
+						<div className="language-picker__icon-inner">en</div>
+					</div>
+					<div className="language-picker__name">
+						<div className="language-picker__name-inner">
+							<div className="language-picker__name-label">English</div>
+							<div className="language-picker__name-change">Change</div>
+						</div>
+					</div>
+				</Focusable>
+			</Card>
+		);
+	};
+}
+export default FocusableExample;
+/** @format */
+/**
+ * External dependencies
+ */
+import React, { Component } from 'react';
+import classNames from 'classnames';
+import { omit } from 'lodash';
+import PropTypes from 'prop-types';
+class Focusable extends Component {
+	static propTypes = {
+		onClick: PropTypes.func.isRequired,
+		onKeyDown: PropTypes.func,
+	};
+	onClick = event => {
+		const { onClick } = this.props;
+		onClick( event );
+	};
+	onKeyDown = event => {
+		const { onClick, onKeyDown = false } = this.props;
+		if ( event.key === 'Enter' || event.key === ' ' ) {
+			event.preventDefault();
+			onClick( event );
+		}
+		if ( onKeyDown ) {
+			onKeyDown( event );
+		}
+	};
+	render = () => {
+		const { children, className } = this.props;
+		const omitProps = [ 'children', 'className', 'onClick', 'onKeyDown', 'role', 'tabIndex' ];
+		const props = omit( this.props, omitProps );
+		return (
+			<div
+				className={ classNames( 'focusable', className ) }
+				role="button"
+				tabIndex="0"
+				onClick={ this.onClick }
+				onKeyDown={ this.onKeyDown }
+				{ ...props }
+			>
+				{ children }
+			</div>
+		);
+	};
+}
+export default Focusable;
+.focusable {
+	.accessible-focus &:focus {
+		box-shadow: 0 0 0 2px $blue-light;
+	}
+}
